import {request} from "../../request/index.js";
import regeneratorRuntime from "../../lib/runtime/runtime";
// pages/goos_detail/index.js
Page({

  /**
   * 页面的初始数据
   */
  data: {
    cartUrl:'data:image/svg+xml;base64,',
    serviceUrl:'data:image/svg+xml;base64,',
    collecturl:'data:image/svg+xml;base64,',
    imageurl:'http://image5.suning.cn/uimg/b2c/newcatentries/0000000000-000000000160455569_1_400x400.jpg',
    pics:[],
    goods_name:'',
    goods_price:'',
    goods_introduce:''
  },
  //全局变量
  //商品详情
    goodDetailData:{},
  //携带的请求参数
  QueryParams:{
    goods_id:0
  },
 
  /**
   * 生命周期函数--监听页面加载
   */
  onLoad: function (options) {
    
    this.QueryParams.goods_id = options.goods_id;
    // const goodsDetail = wx.getStorageSync('goodsDetailData');
    // if(!goodsDetail){
    //   console.log('没有缓存')
    //   this.getGoodsDetailData(this.QueryParams);
    // }else if(Date.now()-goodsDetail.time>1000*60*60){
    //   console.log('缓存过期')
    //   this.getGoodsDetailData(this.QueryParams);
    // }else{
    //   this.goodDetailData = goodsDetail
    // }
    this.getGoodsDetailData(this.QueryParams);
   
  },
   //商品详情页面网络请求
   async getGoodsDetailData (params) {
     
     //使用缓存功能
    const res = await request({url:'/goods/detail',data:params})
    const {pics} = res
    const {goods_name} = res
    const {goods_price} =res
    const {goods_introduce} =res
    this.goodDetailData=res,
     //  wx.setStorageSync('goodsDetailData', {time:Date.now(),date:res});
     this.setData({
       pics,
       goods_name,
       goods_price,
       goods_introduce

     })
    
     console.log(this.goodDetailData)
   },
   //预览轮播图片
   handleSwiper:function(e){
     const urls = this.goodDetailData.pics.map(v=>v.pics_mid_url)
     const index = e.currentTarget.dataset.index;
   
     wx.previewImage({
       current: urls[index],
       urls: urls,
     });
   },

   //添加购物车
   addCart :function(){
     //获取缓存购物车信息
      let cart = wx.getStorageSync('cart')||[]; //就是将它转成一个数组
      //获取这个里面的数据跟商品信息的数据对比对
      let index = cart.findIndex(v=>v.goods_id === this.goodDetailData.goods_id);
      if(index === -1){
        //不存在 商品 ，需要第一次添加
        this.goodDetailData.num =1;
        cart.push(this.goodDetailData)
      } else{
        //已经添加了
        cart[index].num++;
      }   
      //将购物车信息添加到缓存中
      wx.setStorageSync('cart', cart);
      wx.showToast({
        title: '添加成功',
        icon: 'success',
        duration: 1500,
        mask: true,
      });
   },
  /**
   * 生命周期函数--监听页面初次渲染完成
   */
  onReady: function () {

  },

  /**
   * 生命周期函数--监听页面显示
   */
  onShow: function () {

  },

  /**
   * 生命周期函数--监听页面隐藏
   */
  onHide: function () {

  },

  /**
   * 生命周期函数--监听页面卸载
   */
  onUnload: function () {

  },

  /**
   * 页面相关事件处理函数--监听用户下拉动作
   */
  onPullDownRefresh: function () {

  },

  /**
   * 页面上拉触底事件的处理函数
   */
  onReachBottom: function () {

  },

  /**
   * 用户点击右上角分享
   */
  onShareAppMessage: function () {

  }
})